import React, { FC } from "react";
import { Link, useNavigate } from "react-router-dom";

import { useAppDispatch, useAppSelector } from "../../store";
import {
  changeUserActiveStatus,
  uploadUserInfo,
} from "../../slices/userProfileInfo";

import headerStyles from "./Header.module.scss";

const Header: FC = () => {
  const dispatch = useAppDispatch();
  const navigate = useNavigate();
  const isUserLoggedIn = useAppSelector(
    (state) => state.userInfo.isUserLoggedIn
  );

  const getLogoutedUser = (): void => {
    dispatch(uploadUserInfo({ userDatas: {} }));
    dispatch(changeUserActiveStatus({ isUserLoggedIn: false }));
    navigate("/");
  };

  return (
    <div className={headerStyles.items}>
      <a href="/" className={headerStyles.listDecoration}>
        META_BLOG
      </a>
      {!isUserLoggedIn ? (
        <div>
          <Link to="/login" className={headerStyles.listDecoration}>
            Sign In
          </Link>
          <Link
            to="/register"
            className={`${
              (headerStyles.listDecoration,
              headerStyles.listDecoration__signupBackground)
            }`}
          >
            Sign Up
          </Link>
        </div>
      ) : (
        <>
          <a
            className={`${
              (headerStyles.listDecoration, headerStyles.logoutHover)
            }`}
            onClick={getLogoutedUser}
          >
            Logout
          </a>
        </>
      )}
    </div>
  );
};

export default Header;
