import React, { FC } from 'react';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

import { emailRegex } from '../../variables/emailRegex';

import signUpPageStyles from './SignUpPage.module.scss';

const SignUpPage: FC = () => {
  const schema = yup
    .object({
      userName: yup
        .string()
        .min(3)
        .max(20)
        .required(),
      email: yup.string().min(6).max(25).required(),
      password: yup.string().required('Password is required'),
      passwordConfirmation: yup.string()
        .oneOf([yup.ref('password'), null], 'Passwords must match'),
    }).required();
  const {
    register,
    handleSubmit,
    setError,
    clearErrors,
    formState: { errors },
  } = useForm<FormData>({
    resolver: yupResolver(schema),
  });
  const onSubmit = (data: FormData) => console.log(data);

  type FormData = yup.InferType<typeof schema>;

  const onBlurInputs = (e: React.FocusEvent<HTMLInputElement>) => {
    const name = e.target.name;
    const value = e.target.value;

    if (name === 'email') {
      if (!value.match(emailRegex)) {
        setError('email', {
          type: 'manual',
          message: 'Please enter a valid email address',
        });
      } else {
        clearErrors('email');
      }
    }
    if (name === 'password') {
      if (value.length < 6) {
        setError('password', {
          type: 'manual',
          message: 'Your password needs to be at least 6 characters.',
        });
      } else {
        clearErrors('password');
      }
    }
  };
  return (
    <div className={signUpPageStyles.main}>
    <div className={signUpPageStyles.container}>
      <h2 className={signUpPageStyles.title}>Create new account</h2>

      <form className={signUpPageStyles.form} onSubmit={handleSubmit(onSubmit)}>
        <label className={signUpPageStyles.label}>Username</label>
        <input type="text" className={signUpPageStyles.input} placeholder='some-username'
               {...register('userName', { required: 'Username is required' })}
          onBlur={onBlurInputs}
        />

        <label className={signUpPageStyles.label}>Email address</label>
        <input type="text" className={signUpPageStyles.input} placeholder='alex@example.com'
               {...register('email', { required: 'Email is required' })}
        />

        <label className={signUpPageStyles.label}>Password</label>
        <input type="password" className={signUpPageStyles.input} placeholder='Password'
               {...register('password', { required: 'Password is required' })}
               onBlur={onBlurInputs}
        />

        <label className={signUpPageStyles.label}>Repeat Password</label>
        <input type="password" className={signUpPageStyles.input} placeholder='Repeat Password'
               {...register('passwordConfirmation', { required: 'Passwords must match' })}
               onBlur={onBlurInputs}
        />
        <span className={signUpPageStyles.brakeLine} />
        <div className={signUpPageStyles.agreeStatement}>
          <input type="checkbox" id='agree'/>
          <label className={signUpPageStyles.label} htmlFor='agree'>
            I agree to the processing of my personal information
          </label>
        </div>
        <input
          type="submit"
          className={`${signUpPageStyles.input} ${signUpPageStyles.submitButton}`}
          value="Create"
        />
        <h3 className={signUpPageStyles.alreadyHaveAccount}>
          Already have an account?
          <Link to="/login" className={signUpPageStyles.alreadyHaveAccount__link}>Sign In.</Link>
        </h3>
      </form>
    </div>
    </div>
  );
};

export default SignUpPage;
