import React, { FC, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import Heart from 'react-heart';

import { useFullArticleQuery } from '../../slices/fullArticlePage';
import Loader from '../../components/Loader';
import { useAppSelector } from '../../store';
import articleStyles from '../Article/Article.module.scss';

import styles from './FullPageArticle.module.scss';

const FullPageArticle: FC = () => {
  const slug = useAppSelector((state) => state.article.slug) || '';
  const { data, error, isLoading } = useFullArticleQuery({ slug });

  useEffect(() => {
    console.log(data, 'data');
  }, [data]);

  return (
    <>
      {isLoading && <Loader />}
      <div className={styles.main}>
        <div className={styles.container}>
          <header>
            <>
            <h4 className={styles.title}>{data?.article?.title}</h4>

              <Heart animationScale={1.1} onClick={()=>null} className={articleStyles.likeBtn}/>

              {data?.article?.tagList.map((tag) => <span key={tag} className={styles.tag}>{tag}</span>)}
            <p className={styles.description}>{data?.article?.description}</p>
            </>
            <div className={styles.user}></div>
          </header>
          <main className={styles.mainBody}>
            <ReactMarkdown>
              {data?.article?.body || ''}
            </ReactMarkdown>
          </main>
        </div>
      </div>
    </>
  );
};

export default FullPageArticle;
