import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

interface IFullArticle {
  slug: string;
}

interface IFullArticleResponse {
  article: {
    slug: string;
    title: string;
    description: string;
    body: string;
    tagList: string[];
    createdAt: string;
    updatedAt: string;
    favorited: boolean;
    favoritesCount: number;
    author: {
      username: string;
      bio: string;
      image: string;
      following: boolean;
    };
  };
}

const getFullArticle = createApi({
  reducerPath: 'fullArticle',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://api.realworld.io/api/' }),
  endpoints: (builder) => ({
    fullArticle: builder.mutation<IFullArticleResponse, IFullArticle>({
      query: (slug) => {
        return {
          url: `/articles/${slug}`,
          method: 'GET',
          headers: {
            accept: 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify( slug ),
        };
      },
    }),
  }),
});

export const { useFullArticlePage } = getFullArticle;
export default getFullArticle;
