import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export interface IFullArticle {
  slug: string;
}


const fullArticle = createApi({
  reducerPath: 'fullArticle',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://api.realworld.io/api/' }),
  endpoints: (builder) => ({
    fullArticle: builder.mutation<IFullArticle, IFullArticle>({
      query: (slug) => {
        return {
          url: `/articles/${slug}`,
          method: 'POST',
          headers: {
            accept: 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify( slug ),
        };
      },
    }),
  }),
});

export const { useFullArticlePage } = fullArticle;
export default fullArticle;
