import { configureStore, getDefaultMiddleware } from '@reduxjs/toolkit';
import { TypedUseSelectorHook, useDispatch, useSelector } from 'react-redux';

import { rootReducer } from '../reducers/rootReducer';
import { articlesApi } from '../logics/rtkQueryLogics/getArticlesFromApi';
import registerUser from '../slices/userRegistration';
import userLoginApi from '../slices/userLogin';

export type RootState = ReturnType<typeof store.getState>;

const customizedMiddleware = getDefaultMiddleware<RootState>().concat(
  articlesApi.middleware,
  registerUser.middleware,
  userLoginApi.middleware,
);

export const store = configureStore({
  reducer: rootReducer,
  middleware: customizedMiddleware,
});

export type AppDispatch = typeof store.dispatch;
export const useAppDispatch: () => AppDispatch = useDispatch;
export const useAppSelector: TypedUseSelectorHook<RootState> = useSelector;
