import { createApi, Endpoints, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export interface UserLoginDetails {
  email: string;
  password: string;
}

export interface UserResponse {
  user: {
    email: string;
    username: string;
    bio: string;
    image: string | null;
    token: string;
  };
}

const userLogin = createApi({
  reducerPath: 'userLogin',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://api.realworld.io/api/' }),
  endpoints: (builder) => ({
    registerUser: builder.mutation<UserResponse, UserLoginDetails>({
      query: (userLoginDetails) => {
        return {
          url: '/users/login',
          method: 'GET',
          headers: {
            accept: 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(userLoginDetails),
        };
      },
    }),
  }),
});

export const { useLoginUserMutation } = userLogin;

type UserLoginApi = {
  endpoints: {
    registerUser: Endpoints['mutation'];
  };
};

export type UseLoginUserMutation = ReturnType<Endpoints<UserLoginApi>['registerUser']['hooks']['useMutation']>;
export default userLogin;
