import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface IArticleState {
  currentPage?: number;
  totalCountPages: number;
  isLoading?: boolean;
}

const initialState: IArticleState = {
  currentPage: 1,
  totalCountPages: 50,
  isLoading:false,
};

const articleSlice = createSlice({
  name: 'articlePagination',
  initialState,
  reducers: {
    changeArticlePage: (state: IArticleState, action:PayloadAction<number>) => {
      state.currentPage = action.payload;
    },
    getTotalCountPages: (state: IArticleState, action:PayloadAction<number>) => {
      state.totalCountPages = action.payload;
    },
    setLoading: (state: IArticleState, action:PayloadAction<boolean>) => {
      state.isLoading = action.payload;
    },
  },
});

export const { changeArticlePage, getTotalCountPages,setLoading } = articleSlice.actions;
export default articleSlice.reducer;
